         for line in payslip.worked_days_line_ids:
            w_days += line.number_of_days
            t_days += line.x_total_days
         result = round((w_days*(categories.SALARY/t_days)- inputs.TAX_DEDUCTION_WAGE_BY_TIME.amount)*0.13,2)

